import { TextField } from '@material-ui/core';
import React, { useEffect, useState } from 'react';
import Button from './components/Button';
import {MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import './styles.scss';
import { postGenerateTextEndpoint } from './utils';
import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';
import { makeStyles } from '@material-ui/core/styles';
const TITLE = 'Neural pushkin';

function App() {
  const [toggle, setToggle] = useState(false);
  const [text, setText] = useState("");
  //const [model, setModel] = useState('gpt2');
  const model = "345M";
  const [generatedText, postGenerateText] = postGenerateTextEndpoint();


  const useStyles = makeStyles({
    root: {
      width: 300,
    },
  });

  const handleChange = (event) => {
    setText(event.target.value);
  };

  useEffect(() => {
    document.title = TITLE;
  }, []);

  const THEME = createMuiTheme({
    typography: {
     "fontFamily": `"MinionPro", sans-serif`,
     "fontSize": 14,
     "fontWeightLight": 300,
     "fontWeightRegular": 400,
     "fontWeightMedium": 500
    }
  });


  const generateText = () => {
    generatedText.complete = false;
    postGenerateText({ text, model, userId: 1 });
    setToggle(false);
  }

  if (generatedText.complete && !generatedText.error && !toggle){
    setText(text+generatedText.data.result);
    setToggle(true);
  }


  function valuetext(value) {
    return `${value}Â°C`;
  }

  const classes = useStyles();
  return (
  <MuiThemeProvider theme ={THEME}>
  
  <div className='app-container'>
  
  <Typography id="discrete-slider"  fullWidth> Ð£ÑÐ¾Ð²ÐµÐ½Ñ ÐºÑÐµÐ°ÑÐ¸Ð²Ð½Ð¾ÑÑÐ¸ </Typography>
      <Slider
        fullWidth
        defaultValue={1}
        getAriaValueText={valuetext}
        aria-labelledby="discrete-slider"
        valueLabelDisplay="auto"
        step={0.1}
        marks
        min={0.1}
        max={2}
      />
      
    <form noValidate autoComplete='off'>
      <h1>Neural Pushkin</h1>
      <TextField className='form textinput' multiline fullWidth value={text} onChange={handleChange} />
      <Button onClick={generateText} />
    </form>

    {generatedText.pending &&
      <div className='result pending'>ÐÐ¾Ð´Ð¾Ð¶Ð´Ð¸ÑÐµ!</div>}

    {generatedText.complete &&
      (generatedText.error ?
        <div className='result error'>ÐÐµÐ¸Ð·Ð²ÐµÑÑÐ½Ð°Ñ Ð¾ÑÐ¸Ð±ÐºÐ° Ð·Ð°Ð¿ÑÐ¾ÑÐ°</div> :
        <div className='result valid'>
          {generatedText.data.result}
        </div>)}
  </div>
  </MuiThemeProvider>
);

}

export default App;
